// src/resetPassword.js
import React, { useState } from "react";
import './App.css';

function ResetPassword() {
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [step, setStep] = useState(1);
  const [message, setMessage] = useState("");
  const [isSuccess, setIsSuccess] = useState(false);

  const handleSendOTP = async () => {
    const res = await fetch(`${process.env.REACT_APP_API_URL}/send-otp`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email }),
    });
    const data = await res.text();
    setMessage(data);
    setStep(2);
  };

  const handleVerifyOTP = async () => {
    const res = await fetch(`${process.env.REACT_APP_API_URL}/verify-otp`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, otp, newPassword }),
    });
    const data = await res.text();
    if (res.ok) {
      setIsSuccess(true);
      setMessage(data);
    } else {
      setMessage(data);
    }
  };

  if (isSuccess) {
    return (
      <div className="page">
        <h2 className="success-text">ðŸŽ‰ Password Reset Successfully!</h2>
        <img src="/mail.png" className="celebrate-img" alt="Success" />
      </div>
    );
  }

  return (
    <div className="page">
      <h2 className="main-title">Reset Your Password</h2>
      {step === 1 ? (
        <>
          <input
            type="email"
            placeholder="Enter Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <br />
          <button onClick={handleSendOTP}>Send OTP</button>
        </>
      ) : (
        <>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <br />
          <input
            type="password"
            placeholder="New Password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
          <br />
          <button onClick={handleVerifyOTP}>Verify & Reset</button>
        </>
      )}
      <p>{message}</p>
    </div>
  );
}

export default ResetPassword;
