import React, { useState } from "react";
import "./App.css";
import { Howl } from "howler";

const otpSound = new Howl({ src: ["/mail-sound.mp3"] });

function App() {
  const [email, setEmail] = useState("");
  const [otpSent, setOtpSent] = useState(false);

  const sendOtp = () => {
    otpSound.play();
    // your axios POST here
    setOtpSent(true);
  };

  return (
    <div className="app-container">
      <h1 className="title">üîê Reset Password</h1>
      <input
        className="input"
        type="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <button className="send-btn" onClick={sendOtp}>
        Send OTP
      </button>
      {otpSent && <p className="otp-msg">OTP has been sent!</p>}
    </div>
  );
}

export default App;
