import React, { useState } from 'react';
import './App.css';
import { Howl } from 'howler';
import mailSound from './mail-sound.mp3';

function App() {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [stage, setStage] = useState('email'); // email ‚Üí otp
  const [message, setMessage] = useState('');

  const playSound = () => {
    const sound = new Howl({ src: [mailSound] });
    sound.play();
  };

  const handleSendOTP = async () => {
    try {
      const res = await fetch('http://localhost:5000/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });

      const data = await res.text();

      if (res.ok) {
        setMessage(`‚úÖ ${data}`);
        playSound();
        setStage('otp'); // ‚û°Ô∏è move to OTP screen
      } else {
        setMessage(`‚ùå ${data}`);
      }
    } catch (err) {
      console.error(err);
      setMessage('‚ùå Error occurred.');
    }
  };

  const handleVerifyOTP = async () => {
    try {
      const res = await fetch('http://localhost:5000/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, otp, newPassword }),
      });

      const data = await res.text();

      if (res.ok) {
        setMessage(`‚úÖ ${data}`);
      } else {
        setMessage(`‚ùå ${data}`);
      }
    } catch (err) {
      console.error(err);
      setMessage('‚ùå Error occurred.');
    }
  };

  return (
    <div className="container">
      <h1>üîê OTP Verification</h1>

      {stage === 'email' ? (
        <>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <button onClick={handleSendOTP}>Send OTP</button>
        </>
      ) : (
        <>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <input
            type="password"
            placeholder="Enter new password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
          <button onClick={handleVerifyOTP}>Verify & Reset Password</button>
        </>
      )}

      {message && <p style={{ marginTop: '20px' }}>{message}</p>}
    </div>
  );
}

export default App;
