import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [stage, setStage] = useState("request"); // request | verify

  const sendOtp = async () => {
    try {
      await axios.post("http://localhost:5000/send-otp", { email });
      alert("OTP sent to your email");
      setStage("verify");
    } catch (err) {
      alert("Error sending OTP");
    }
  };

  const verifyOtp = async () => {
    try {
      await axios.post("http://localhost:5000/verify-otp", {
        email,
        otp,
        newPassword,
      });
      alert("Password changed successfully!");
      setStage("request");
      setEmail("");
      setOtp("");
      setNewPassword("");
    } catch (err) {
      alert("Invalid OTP or error occurred");
    }
  };

  return (
    <div className="App" style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>üîê Reset Your Password</h2>
      <input
        type="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <br /><br />
      {stage === "request" && (
        <button onClick={sendOtp}>Send OTP</button>
      )}
      {stage === "verify" && (
        <>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <br /><br />
          <input
            type="password"
            placeholder="Enter new password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
          <br /><br />
          <button onClick={verifyOtp}>Verify OTP & Reset Password</button>
        </>
      )}
    </div>
  );
}

export default App;
